let raw = $input.first().json.output;

// Enhanced cleanup for various AI response formats
raw = raw
  .replace(/^```(?:json)?\n/, '')
  .replace(/\n```$/, '')
  .replace(/^(Here is the JSON:|Here is your JSON:)\s*/, '')
  .replace(/^(Here are the job listings:|Job listings:)\s*/, '')
  .replace(/^\s*\[?\s*/, '') 
  .replace(/\s*\]?\s*$/, '') 
  .trim();

// Add brackets if missing
if (!raw.startsWith('[')) {
  raw = '[' + raw + ']';
}

let jobs;
try {
  jobs = JSON.parse(raw);
} catch (err) {
  try {
    let fixedRaw = raw
      .replace(/,\s*]/g, ']')
      .replace(/,\s*}/g, '}')
      .replace(/}\s*{/g, '},{');

    jobs = JSON.parse(fixedRaw);
  } catch (err2) {
    throw new Error('Invalid JSON after cleanup: ' + err2.message + '\nRaw output: ' + raw);
  }
}

// Ensure jobs is an array
if (!Array.isArray(jobs)) {
  jobs = [jobs];
}

// SIMPLE LINK VALIDATION
function isValidUrl(url) {
  if (!url || typeof url !== 'string') return false;

  url = url.trim();

  // Basic checks
  if (url.length < 4) return false;
  if (url.includes(' ') || url.includes('\n')) return false;

  // Must contain a dot for domain
  if (!url.includes('.')) return false;

  // Simple URL pattern
  return /^(https?:\/\/)?[a-zA-Z0-9.-]+\.[a-zA-Z]{2,}/.test(url);
}

function normalizeUrl(url) {
  if (!url) return '';

  url = url.trim();

  // Add https if missing
  if (url.startsWith('www.')) {
    url = 'https://' + url;
  } else if (!url.startsWith('http://') && !url.startsWith('https://')) {
    if (url.includes('.')) {
      url = 'https://' + url;
    }
  }

  return url;
}

// FILTER AND VALIDATE JOBS
const preferredJobs = [];
const otherJobs = [];

jobs.forEach(job => {
  const url = job.apply_link;

  // Skip jobs without apply links
  if (!url) return;

  // Validate and normalize URL
  const normalizedUrl = normalizeUrl(url);
  if (!isValidUrl(normalizedUrl)) return;

  // Update the job with normalized URL
  job.apply_link = normalizedUrl;

  // Categorize jobs - preferred portals first
  const portal = (job.job_portal || '').toLowerCase();
  const link = normalizedUrl.toLowerCase();

  const isPreferred = link.includes('linkedin.com') || 
                     link.includes('indeed.com') || 
                     link.includes('naukri.com') ||
                     portal.includes('linkedin') ||
                     portal.includes('indeed') ||
                     portal.includes('naukri');

  if (isPreferred) {
    preferredJobs.push(job);
  } else {
    otherJobs.push(job);
  }
});

// Combine all valid jobs - preferred first
const finalJobs = [...preferredJobs, ...otherJobs];

const items = [];
if (finalJobs.length === 0) {
  items.push({
    json: {
      chatId: $input.first().json.chatId,
      text: '<b>‚ùå Sorry, No job listings found with valid application links.</b>\n\nPlease try a different keyword or location to improve your results.' ,
      parse_mode: 'HTML',
    }
  });
  return items;
}

const chatId = $input.first().json.chatId;
const MAX_TEXT_LEN = 4000;
const sliceCount = Math.min(finalJobs.length, 20);

for (let i = 0; i < sliceCount; i++) {
  const j = finalJobs[i];

  let text = `<b>${i+1}. ${j.title || 'No Title'}</b>\n`;

  if (j.company) text += `üè¢ <b>Company</b>: ${j.company}\n`;
  if (j.location) text += `üìç <b>Location</b>: ${j.location}\n`;
  if (j.job_portal) text += `üåê <b>Portal</b>: ${j.job_portal}\n`;
  if (j.date_posted) text += `üìÖ <b>Posted</b>: ${j.date_posted}\n`;
  if (j.employment_type) text += `üíº <b>Type</b>: ${j.employment_type}\n`;
  if (j.experience_required) text += `‚≠ê <b>Experience</b>: ${j.experience_required}\n`;
  if (j.salary && j.salary !== 'Not specified') text += `üí∞ <b>Salary</b>: ${j.salary}\n`;
  if (j.skills_required) {
    const skills = j.skills_required.length > 1500 ? 
      j.skills_required.substring(0, 1500) + '...' : 
      j.skills_required;
    text += `üîß <b>Skills</b>: ${skills}\n`;
  }

  text += `<a href="${j.apply_link}">üîó <b>Apply Here</b></a>\n\n`;

  if (text.length > MAX_TEXT_LEN) {
    text = text.substring(0, MAX_TEXT_LEN - 20) + '\n‚Ä¶(truncated)';
  }

  items.push({
    json: {
      chatId,
      text,
      parse_mode: 'HTML',
    }
  });
}

return items;

