const raw = $input.first().json.data || "";

const cleaned = raw
  .replace(/\\r\\n|\\n|\\r/g, '\n')                      // Fix newlines
  .replace(/([a-zA-Z])\s([a-zA-Z])/g, '$1$2')           // Join broken words
  .replace(/\s{2,}/g, ' ')                              // Remove multiple spaces
