  .replace(/[‚Ä¢‚àö\-‚Äì‚óè‚ñ™Ô∏è]/g, '')                           // ‚úÖ Remove bullets
  .replace(/\[.*?\]/g, '')                              // Remove things like [Link]
  .trim();

return [{ json: { cleanedResume: cleaned } }];

